def find_brackets(brackets):
    open_bracket = '('
    close_bracket = ')'
    count = 0

    for index, el in enumerate(brackets):
        if el == open_bracket:
            count += 1
        if el == close_bracket:
            count -= 1
        if count < 0:
            count = index
            break

    return count + 1


